  return sommeIncrease(data)
}
function result2() {

  const dataWindows = data.map((courant, i) => i<(data.length - 2) && data[i] + data[i+1] + data[i+2]).filter(d=>d)
  console.log(dataWindows)
  const dataFin = dataWindows.map((courant, i) => i>0 && dataWindows[i-1] < courant ? 1:0)
  console.log(dataFin)
  const result = dataFin.reduce((accumulator, currentValue) =>
  accumulator + currentValue)
  return (
    result
  )
}

export default function getResultats() {
  return [result1(), result2()]
}
