import { reducerSum } from "util"
import { input } from "../data/input1"
// const input = ''
const data = input.split("\n").map((i) => parseInt(i))

function result1() {
  const dataFin = data.map((courant, i) => i>=1 && data[i-1] > courant ? 1:0)
  const result = dataFin.reduce((accumulator, currentValue) =>
  accumulator + currentValue)
  return result
}
function result2() {
  let resultatFinal = []
  return (
    resultatFinal[0] *
    resultatFinal[1] *
    (2020 - resultatFinal[0] - resultatFinal[1])
  )
}

export default function getResultats() {
  return [result1(), result2()]
}
